mapper.update:
UPDATE BOARD 
SET B_TITLE =#{bTitle}, B_CONTENT =#{bContent}
WHERE B_KEY = #{boardKey}

mapper.select:
SELECT COUNT(*) AS LISTCOUNT
FROM BOARD
WHERE B_CATEGORY = #{bCateGory}

mapper.update:
UPDATE BOARD
SET B_COUNT = B_COUNT + 1
WHERE B_KEY = #{boardKey}

UPDATE BOARD_COMMENT
SET CO_CONTENT = #{cContent} 
WHERE CO_KEY = #{commentKey}
